const getData=(t=5)=>{const e=document.querySelector(".list"),n=(t,e)=>(t=Math.ceil(t),e=Math.floor(e),Math.floor(Math.random()*(e-t+1))+t);fetch("https://jsonplaceholder.typicode.com/todos").then((t=>t.json())).catch((t=>console.log(t))).then((l=>{for(let o=0;o<t;o++){let t=n(0,l.length-1),o=n(0,l.length-1),a=n(1,4);e.insertAdjacentHTML("beforeend",`<li>       \n          <label for=${l[t].id}\n            >           \n            <input type="checkbox" id=${l[t].id} />\n            <p class="test">${l[t].title}</p>\n            <span class="checkmark"></span>\n            ${1===a?`<span>${l[o].title}</span>`:""}          \n          </label>        \n      </li>`)}}))};getData();