const container=document.querySelector(".list");let q=5;function getRandomIntInclusive(n,t){return n=Math.ceil(n),t=Math.floor(t),Math.floor(Math.random()*(t-n+1))+n}const data=fetch("https://jsonplaceholder.typicode.com/todos").then((n=>n.json())).catch((n=>alert(n))).then((n=>{for(let t=0;t<q;t++){let t=getRandomIntInclusive(0,n.length),e=getRandomIntInclusive(0,n.length),a=getRandomIntInclusive(1,4);container.insertAdjacentHTML("beforeend",`<li>\n        <div class="card__item">\n          <label for=${n[t].id}\n            >           \n            <input type="checkbox" id=${n[t].id} />\n            <p class="test">${n[t].title}</p>\n            <span class="checkmark"></span>\n            ${1===a?`<span>${n[e].title}</span>`:""}          \n          </label>\n        </div>\n      </li>`)}}));